import React from "react";
import { useCustomization } from "./context/CustomizationContext";
import { customizationData } from "../data/customizationData";

const BrandSelection = () => {
  const { customization, updateCustomization } = useCustomization();

  const handleBrandClick = (brand) => {
    updateCustomization("brand", brand.name);
    updateCustomization("case", "");
    updateCustomization("band", "");
  };

  return (
    <div className="section">
      <h2>Select Brand</h2>
      <div className="options">
        {customizationData.brands.map((brand) => (
          <div
            key={brand.name}
            className={`option ${customization.brand === brand.name ? "selected" : ""}`}
            onClick={() => handleBrandClick(brand)}
          >
            <img src={brand.image} alt={brand.name} />
            <p>{brand.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BrandSelection;
