import React from "react";
import { useCustomization } from "./context/CustomizationContext";
import { customizationData } from "../data/customizationData";

const SizeSelection = () => {
  const { customization, updateCustomization } = useCustomization();

  const selectedBrandData = customizationData.brands.find(
    (brand) => brand.name === customization.brand
  );

  if (!selectedBrandData) {
    return <p>Please select a brand first.</p>;
  }

  return (
    <div className="section" style={{textAlign:'center'}}>
      <h2>Select Size</h2>
      <div className="options">
        {selectedBrandData.sizes.map((size) => (
          <div
            key={size.id}
            className={`option ${customization.size === size.name ? "selected" : ""}`}
            onClick={() => updateCustomization("size", size.name)}
          >
            <img src={size.image} alt={size.name} />
            <p>{size.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SizeSelection;
