import React from "react";
import { useCustomization } from "./context/CustomizationContext";
import { customizationData } from "./data/customizationData";

const BandSelection = () => {
  const { customization, updateCustomization } = useCustomization();

  const selectedBrandData = customizationData.brands.find(
    (brand) => brand.name === customization.brand
  );

  if (!selectedBrandData) {
    return <p>Please select a brand first.</p>;
  }

  return (
    <div className="section">
      <h2>Select Band</h2>
      <div className="options">
        {selectedBrandData.bands.map((band) => (
          <div
            key={band.id}
            className={`option ${customization.band === band.type ? "selected" : ""}`}
            onClick={() => updateCustomization("band", band.type)}
          >
            <img src={band.image} alt={band.type} />
            <p>{band.type}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BandSelection;
