import React from 'react';
import { useCustomization } from '../Context/CustomizationContext';

const cases = ['Stainless Steel', 'Aluminum', 'Titanium'];

const CaseSelection = () => {
  const { customization, updateCustomization } = useCustomization();

  return (
    <div>
      <h2>Select Case</h2>
      <div style={{ display: 'flex', gap: '10px' }}>
        {cases.map((caseType) => (
          <button
            key={caseType}
            style={{
              padding: '10px',
              border: customization.caseType === caseType ? '2px solid blue' : '1px solid gray',
            }}
            onClick={() => updateCustomization('caseType', caseType)}
          >
            {caseType}
          </button>
        ))}
      </div>
    </div>
  );
};

export default CaseSelection;
