import React from 'react';
import { useCustomization } from '../Context/CustomizationContext';

const bands = ['Sport Band', 'Leather', 'Solo Loop'];

const BandSelection = () => {
  const { customization, updateCustomization } = useCustomization();

  return (
    <div>
      <h2>Select Band</h2>
      <div style={{ display: 'flex', gap: '10px' }}>
        {bands.map((band) => (
          <button
            key={band}
            style={{
              padding: '10px',
              border: customization.band === band ? '2px solid blue' : '1px solid gray',
            }}
            onClick={() => updateCustomization('band', band)}
          >
            {band}
          </button>
        ))}
      </div>
    </div>
  );
};

export default BandSelection;
