import React from "react";
import { useCustomization } from "./context/CustomizationContext";

const Preview = () => {
  const { customization } = useCustomization();

  return (
    <div className="section preview">
      <h2>Preview</h2>
      <div className="preview-details">
        {customization.brand && <p><strong>Brand:</strong> {customization.brand}</p>}
        {customization.case && <p><strong>Case:</strong> {customization.case}</p>}
        {customization.band && <p><strong>Band:</strong> {customization.band}</p>}
        {customization.price && <p><strong>Price:</strong> {customization.price}</p>}
      </div>
      {customization.brand && customization.case && customization.band && (
        <img
          src={`/${customization.brand}-${customization.case}-${customization.band}.png`}
          alt="Preview"
        />
      )}
    </div>
  );
};

export default Preview;
